import React, { useState, useCallback, useEffect } from "react";
import ReactFlow, {
  addEdge,
  Background,
  Controls,
  Panel,
  useNodesState,
  useEdgesState,
} from "reactflow";
import "reactflow/dist/style.css";
import { Button } from "./ui/button";
import { toast } from "sonner";
import { Card, CardContent } from "./ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "./ui/tabs";
import AddCourseDialog from "./AddCourseDialog";
import {
  getCourseRoadmap,
  saveCourseRoadmap,
} from "../services/courseRoadmapService";
import CourseNodeComponent from "./CourseNodeComponent";
import NodeColorPicker from "./NodeColorPicker";
import NodeActions from "./NodeActions";

// Define node types
const nodeTypes = {
  courseNode: CourseNodeComponent,
};

export default function CourseRoadmapEditor({ courseId, readOnly = false }) {
  // State for nodes and edges
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  // State for dialogs
  const [showAddCourseDialog, setShowAddCourseDialog] = useState(false);
  const [selectedNode, setSelectedNode] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Load course roadmap data
  useEffect(() => {
    const loadCourseRoadmap = async () => {
      if (!courseId) return;

      try {
        setIsLoading(true);
        setError(null);

        const roadmapData = await getCourseRoadmap(courseId);

        if (roadmapData.nodes?.length) {
          setNodes(roadmapData.nodes);
        }
        if (roadmapData.edges?.length) {
          setEdges(roadmapData.edges);
        }
      } catch (error) {
        console.error("Error loading course roadmap:", error);
        setError("Failed to load roadmap data");
        toast.error("Failed to load roadmap data");
      } finally {
        setIsLoading(false);
      }
    };

    loadCourseRoadmap();
  }, [courseId, setNodes, setEdges]);

  // Handle node click
  const onNodeClick = useCallback(
    (event, node) => {
      if (!editMode) return;
      setSelectedNode(node);
    },
    [editMode]
  );

  // Handle adding a node
  const onAddNode = useCallback(
    (nodeData) => {
      // Create new node
      const newNode = {
        id: `node-${Date.now()}`,
        type: "courseNode",
        position: {
          x: Math.random() * 400,
          y: Math.random() * 400,
        },
        data: {
          ...nodeData,
          nodeBgColor: nodeData.nodeBgColor || "#1e1e2f",
          nodeColor: nodeData.nodeColor || "#7c3aed",
          textColor: nodeData.textColor || "#ffffff",
        },
      };

      setNodes((nds) => nds.concat(newNode));
      setShowAddCourseDialog(false);
      toast.success("Node added successfully");
    },
    [setNodes]
  );

  // Handle connecting nodes
  const onConnect = useCallback(
    (params) => {
      if (!editMode) return;

      setEdges((eds) =>
        addEdge(
          {
            ...params,
            type: "smoothstep",
            animated: false,
            style: { stroke: "#6d28d9", strokeWidth: 2 },
          },
          eds
        )
      );
    },
    [setEdges, editMode]
  );

  // Handle node deletion
  const handleNodeDelete = useCallback(
    (nodeId) => {
      if (!editMode) return;

      setNodes((nds) => nds.filter((node) => node.id !== nodeId));
      setEdges((eds) =>
        eds.filter((edge) => edge.source !== nodeId && edge.target !== nodeId)
      );

      setSelectedNode(null);
      toast.success("Node deleted successfully");
    },
    [setNodes, setEdges]
  );

  // Handle node update
  const handleNodeUpdate = useCallback(
    (nodeId, updatedData) => {
      if (!editMode) return;

      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === nodeId) {
            return {
              ...node,
              data: {
                ...node.data,
                ...updatedData,
              },
            };
          }
          return node;
        })
      );

      setSelectedNode(null);
      toast.success("Node updated successfully");
    },
    [setNodes]
  );

  // Handle node style update
  const handleNodeStyleUpdate = useCallback(
    (nodeId, styleData) => {
      if (!editMode) return;

      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === nodeId) {
            return {
              ...node,
              data: {
                ...node.data,
                ...styleData,
              },
            };
          }
          return node;
        })
      );
    },
    [setNodes]
  );

  // Handle save roadmap
  const handleSaveRoadmap = useCallback(async () => {
    if (!courseId) {
      toast.error("Invalid course ID");
      return;
    }

    try {
      // Show loading toast
      const loadingToastId = toast.loading("Đang lưu lộ trình...");

      console.log("Attempting to save roadmap for course:", courseId);
      console.log("Nodes to save:", nodes);
      console.log("Edges to save:", edges);

      // Convert courseId to a number if it's a string
      const numericCourseId =
        typeof courseId === "string" ? parseInt(courseId, 10) : courseId;

      // Check if courseId is valid after conversion
      if (isNaN(numericCourseId)) {
        toast.dismiss(loadingToastId);
        toast.error("ID khóa học không hợp lệ");
        return;
      }

      // Validate nodes and edges
      const validNodes = nodes.filter((node) => node.id && node.position);
      if (validNodes.length < nodes.length) {
        console.warn(
          `Found ${
            nodes.length - validNodes.length
          } invalid nodes, they will be skipped`
        );
      }

      // Ensure every edge has valid source and target
      const validEdges = edges.filter((edge) => edge.source && edge.target);
      if (validEdges.length < edges.length) {
        console.warn(
          `Found ${
            edges.length - validEdges.length
          } invalid edges, they will be skipped`
        );
      }

      // Save the roadmap data
      const result = await saveCourseRoadmap(numericCourseId, {
        nodes: validNodes,
        edges: validEdges,
      });

      console.log("Save result:", result);

      // Hide loading toast and show success
      toast.dismiss(loadingToastId);
      toast.success("Lưu lộ trình thành công!");

      // Update the displayed data with the saved data
      if (result.nodes?.length) {
        setNodes(result.nodes);
      }
      if (result.edges?.length) {
        setEdges(result.edges);
      }
    } catch (error) {
      console.error("Error saving roadmap:", error);

      // Determine detailed error message
      let errorDetails = "";
      if (error.response) {
        errorDetails =
          error.response.data?.message ||
          `Error ${error.response.status}: ${error.response.statusText}`;
      } else if (error.message) {
        errorDetails = error.message;
      }

      toast.dismiss();
      toast.error(`Lỗi khi lưu lộ trình: ${errorDetails}`, {
        duration: 5000,
        action: {
          label: "Thử lại",
          onClick: () => handleSaveRoadmap(),
        },
      });
    }
  }, [courseId, nodes, edges, setNodes, setEdges]);

  // Toggle edit mode
  const toggleEditMode = useCallback(() => {
    setEditMode((prev) => !prev);
    setSelectedNode(null);
  }, []);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full">
        Loading roadmap...
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col items-center justify-center h-full">
        <p className="text-red-500">{error}</p>
        <Button onClick={() => window.location.reload()} className="mt-4">
          Retry
        </Button>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={editMode ? onNodesChange : undefined}
        onEdgesChange={editMode ? onEdgesChange : undefined}
        onConnect={onConnect}
        onNodeClick={onNodeClick}
        nodeTypes={nodeTypes}
        fitView
      >
        <Background color="#4a5568" gap={16} />
        <Controls />

        {/* Editor Panels */}
        {!readOnly && (
          <Panel
            position="top-left"
            className="bg-cyberpunk-darker p-2 rounded-md shadow-lg"
          >
            <div className="flex flex-col gap-2">
              <Button
                onClick={toggleEditMode}
                variant={editMode ? "default" : "outline"}
                className={`${
                  editMode
                    ? "bg-purple-600 hover:bg-purple-700 text-white"
                    : "border-purple-500/30 bg-transparent hover:bg-purple-900/20 hover:border-purple-500/50 text-gray-300"
                }`}
                size="sm"
              >
                {editMode ? "Editing Mode: ON" : "Editing Mode: OFF"}
              </Button>

              {editMode && (
                <>
                  <Button
                    onClick={() => setShowAddCourseDialog(true)}
                    variant="outline"
                    className="border-purple-500/30 bg-transparent hover:bg-purple-900/20 hover:border-purple-500/50 text-gray-300"
                    size="sm"
                  >
                    Add Node
                  </Button>

                  <Button
                    onClick={handleSaveRoadmap}
                    className="bg-green-600 hover:bg-green-700 text-white"
                    size="sm"
                  >
                    Save Roadmap
                  </Button>
                </>
              )}
            </div>
          </Panel>
        )}

        {/* Node actions panel when a node is selected */}
        {editMode && selectedNode && (
          <Panel
            position="top-right"
            className="bg-cyberpunk-darker p-2 rounded-md shadow-lg"
          >
            <Card className="bg-cyberpunk-darker border-purple-500/30">
              <CardContent className="p-4">
                <Tabs defaultValue="actions">
                  <TabsList className="bg-cyberpunk-dark grid grid-cols-2 mb-4">
                    <TabsTrigger
                      value="actions"
                      className="data-[state=active]:bg-purple-900"
                    >
                      Actions
                    </TabsTrigger>
                    <TabsTrigger
                      value="style"
                      className="data-[state=active]:bg-purple-900"
                    >
                      Style
                    </TabsTrigger>
                  </TabsList>

                  <TabsContent value="actions">
                    <NodeActions
                      node={selectedNode}
                      onUpdate={handleNodeUpdate}
                      onDelete={handleNodeDelete}
                      onClose={() => setSelectedNode(null)}
                    />
                  </TabsContent>

                  <TabsContent value="style">
                    <NodeColorPicker
                      node={selectedNode}
                      onStyleUpdate={handleNodeStyleUpdate}
                    />
                  </TabsContent>
                </Tabs>
              </CardContent>
            </Card>
          </Panel>
        )}
      </ReactFlow>

      {/* Add Course Dialog */}
      <AddCourseDialog
        open={showAddCourseDialog}
        onClose={() => setShowAddCourseDialog(false)}
        onAdd={onAddNode}
      />
    </div>
  );
}
