"use client";

import { useState, useCallback, useEffect, useRef } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  Panel,
  ReactFlowProvider,
  useReactFlow,
  MarkerType,
  Connection,
} from "reactflow";
import "reactflow/dist/style.css";
import { Tabs, TabsList, TabsTrigger } from "./ui/tabs";
import { Button } from "./ui/button";
import { useToast } from "./ui/use-toast";
import {
  PlusCircle,
  Save,
  Trash,
  Plus,
  X,
  Link as LinkIcon,
  LinkOff,
  Palette,
} from "lucide-react";
import CourseNode from "./CourseNode";
import CourseInfoPanel from "./CourseInfoPanel";
import AddCourseDialog from "./AddCourseDialog";
import RoadmapEditorPanel from "./RoadmapEditorPanel";

const nodeTypes = {
  courseNode: CourseNode,
};

function RoadmapViewInner({
  id,
  initialNodes = [],
  initialEdges = [],
  isEditing = false,
  readOnly = false,
  onSave,
}) {
  const { toast } = useToast();
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNode, setSelectedNode] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [isAddCourseOpen, setIsAddCourseOpen] = useState(false);
  const [editMode, setEditMode] = useState("select"); // select, connect, add, delete
  const [connectionType, setConnectionType] = useState("arrow"); // arrow, none
  const [showEditorPanel, setShowEditorPanel] = useState(false);
  const reactFlowWrapper = useRef(null);
  const reactFlowInstance = useReactFlow();
  const [undoStack, setUndoStack] = useState([]);
  const [redoStack, setRedoStack] = useState([]);

  // Set initial nodes and edges when they change
  useEffect(() => {
    if (initialNodes.length > 0) {
      setNodes(
        initialNodes.map((node) => ({
          ...node,
          type: "courseNode",
          data: {
            ...node.data,
            showQuickToggle: true,
            onQuickToggle: handleCompleteToggle,
          },
        }))
      );
    }
    if (initialEdges.length > 0) {
      setEdges(initialEdges);
    }
  }, [initialNodes, initialEdges, setNodes, setEdges]);

  // Save current state for undo feature
  const saveCurrentState = useCallback(() => {
    setUndoStack((prev) => [...prev, { nodes: [...nodes], edges: [...edges] }]);
    setRedoStack([]);
  }, [nodes, edges]);

  const onConnect = useCallback(
    (params) => {
      saveCurrentState();
      // Add marker and apply current edge style settings
      const newEdge = {
        ...params,
        id: `edge-${Date.now()}`,
        type: "smoothstep",
        animated: true,
        style: { stroke: "#6d28d9" },
        // Only add marker if connection type is arrow
        ...(connectionType === "arrow" && {
          markerEnd: {
            type: MarkerType.ArrowClosed,
            width: 20,
            height: 20,
            color: "#6d28d9",
          },
        }),
        data: {
          connectionType,
        },
      };
      setEdges((eds) => addEdge(newEdge, eds));

      toast({
        title: "Connection added",
        description: "A new connection has been created between nodes",
      });
    },
    [setEdges, saveCurrentState, toast, connectionType]
  );

  const onNodeClick = useCallback(
    (event, node) => {
      event.stopPropagation();

      if (editMode === "delete") {
        // Delete mode: remove the node
        saveCurrentState();
        setNodes(nodes.filter((n) => n.id !== node.id));
        // Also remove any connected edges
        setEdges(
          edges.filter((e) => e.source !== node.id && e.target !== node.id)
        );

        toast({
          title: "Node deleted",
          description: `${node.data.label} has been removed from the roadmap`,
        });
      } else if (editMode === "connect" && selectedNode) {
        // Connect mode: create an edge between selectedNode and this node
        if (selectedNode.id !== node.id) {
          const newEdge = {
            id: `edge-${Date.now()}`,
            source: selectedNode.id,
            target: node.id,
            type: "smoothstep",
            animated: true,
            style: { stroke: "#6d28d9" },
            ...(connectionType === "arrow" && {
              markerEnd: {
                type: MarkerType.ArrowClosed,
                width: 20,
                height: 20,
                color: "#6d28d9",
              },
            }),
            data: {
              connectionType,
            },
          };

          saveCurrentState();
          setEdges((eds) => [...eds, newEdge]);
          setSelectedNode(null);
          setEditMode("select");

          toast({
            title: "Connection created",
            description: `Connected ${selectedNode.data.label} to ${node.data.label}`,
          });
        }
      } else if (editMode === "select") {
        // Regular select mode: show node details
        setSelectedNode(node);
      }
    },
    [
      editMode,
      selectedNode,
      nodes,
      edges,
      setNodes,
      setEdges,
      saveCurrentState,
      toast,
    ]
  );

  const onPaneClick = useCallback(() => {
    // In regular mode, clicking the pane should deselect the node
    setSelectedNode(null);

    // If in connect mode but no node selected, return to select mode
    if (editMode === "connect" && selectedNode) {
      setEditMode("select");
      setSelectedNode(null);
      toast({
        title: "Connection cancelled",
        description: "Node connection has been cancelled",
      });
    }
  }, [editMode, selectedNode, toast]);

  const handleCompleteToggle = useCallback(
    (nodeId, completed) => {
      if (readOnly) return;

      // Get the current timestamp
      const completedAt = completed ? new Date().toISOString() : null;

      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === nodeId) {
            return {
              ...node,
              data: {
                ...node.data,
                completed,
                completedAt,
              },
            };
          }
          return node;
        })
      );

      toast({
        title: completed
          ? "Course marked as completed"
          : "Course marked as incomplete",
        description: `You've ${completed ? "completed" : "unmarked"} ${
          nodes.find((n) => n.id === nodeId)?.data.label
        }`,
      });
    },
    [nodes, setNodes, toast, readOnly]
  );

  const handleSaveRoadmap = useCallback(() => {
    if (onSave) {
      onSave(nodes, edges);
    } else {
      toast({
        title: "Roadmap saved",
        description: "Your roadmap has been saved successfully",
      });
    }
  }, [nodes, edges, onSave, toast]);

  const handleAddCourse = useCallback(
    (courseData) => {
      saveCurrentState();

      // Find a position that doesn't overlap with existing nodes
      let position = { x: 100, y: 100 };

      // If we have a reactFlowInstance, try to center the new node in the viewport
      if (reactFlowInstance) {
        const { x, y, zoom } = reactFlowInstance.getViewport();
        position = reactFlowInstance.screenToFlowPosition({
          x: window.innerWidth / 2,
          y: window.innerHeight / 2,
        });

        // Avoid overlapping with existing nodes by adding some offset
        const existingPositions = nodes.map((node) => node.position);
        if (
          existingPositions.some(
            (pos) =>
              Math.abs(pos.x - position.x) < 100 &&
              Math.abs(pos.y - position.y) < 100
          )
        ) {
          position.x += 150;
          position.y += 50;
        }
      }

      const newNodeId = `node-${Date.now()}`;
      const newNode = {
        id: newNodeId,
        type: "courseNode",
        position,
        data: {
          ...courseData,
          completed: false,
          completedAt: null,
          showQuickToggle: true,
          onQuickToggle: handleCompleteToggle,
          id: newNodeId,
        },
      };

      setNodes((nds) => [...nds, newNode]);
      setIsAddCourseOpen(false);

      toast({
        title: "Course added",
        description: `${courseData.label} has been added to the roadmap`,
      });
    },
    [
      setNodes,
      toast,
      reactFlowInstance,
      nodes,
      handleCompleteToggle,
      saveCurrentState,
    ]
  );

  const handleDeleteNode = useCallback(
    (nodeId) => {
      saveCurrentState();

      // Remove the node
      setNodes((nds) => nds.filter((node) => node.id !== nodeId));
      // Remove connected edges
      setEdges((eds) =>
        eds.filter((edge) => edge.source !== nodeId && edge.target !== nodeId)
      );

      setSelectedNode(null);

      toast({
        title: "Node deleted",
        description: "The node has been removed from the roadmap",
      });
    },
    [setNodes, setEdges, toast, saveCurrentState]
  );

  const handleUpdateNodeStyle = useCallback(
    (nodeId, style) => {
      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === nodeId) {
            return {
              ...node,
              data: {
                ...node.data,
                ...style,
              },
            };
          }
          return node;
        })
      );
    },
    [setNodes]
  );

  const onEdgeClick = useCallback(
    (event, edge) => {
      if (editMode === "delete") {
        saveCurrentState();

        // Delete the edge
        setEdges((eds) => eds.filter((e) => e.id !== edge.id));

        toast({
          title: "Connection removed",
          description: "The connection between nodes has been removed",
        });
      }
    },
    [setEdges, editMode, saveCurrentState, toast]
  );

  const handleUndo = useCallback(() => {
    if (undoStack.length === 0) return;

    const currentState = { nodes: [...nodes], edges: [...edges] };
    const previousState = undoStack[undoStack.length - 1];

    setRedoStack((prev) => [...prev, currentState]);
    setUndoStack((prev) => prev.slice(0, -1));

    setNodes(previousState.nodes);
    setEdges(previousState.edges);

    toast({
      title: "Undo",
      description: "Previous action undone",
    });
  }, [undoStack, redoStack, nodes, edges, setNodes, setEdges, toast]);

  const handleRedo = useCallback(() => {
    if (redoStack.length === 0) return;

    const currentState = { nodes: [...nodes], edges: [...edges] };
    const nextState = redoStack[redoStack.length - 1];

    setUndoStack((prev) => [...prev, currentState]);
    setRedoStack((prev) => prev.slice(0, -1));

    setNodes(nextState.nodes);
    setEdges(nextState.edges);

    toast({
      title: "Redo",
      description: "Action redone",
    });
  }, [undoStack, redoStack, nodes, edges, setNodes, setEdges, toast]);

  // Update node positions when they are dragged
  const onNodeDragStop = useCallback(() => {
    if (isEditing) {
      saveCurrentState();
    }
  }, [isEditing, saveCurrentState]);

  // Handle category filtering
  const filteredNodes = useCallback(() => {
    if (selectedCategory === "all") {
      return nodes;
    }
    return nodes.filter(
      (node) =>
        node.data.category?.toLowerCase() === selectedCategory.toLowerCase()
    );
  }, [nodes, selectedCategory]);

  const handleStyleChange = useCallback(
    (styles) => {
      // Apply styles to selected node or default edge options
      if (selectedNode) {
        handleUpdateNodeStyle(selectedNode.id, styles.nodeStyle);
      }

      // Update default edge options
      // (would be implemented in a full version)
    },
    [selectedNode, handleUpdateNodeStyle]
  );

  const handleConnectionTypeChange = useCallback((type) => {
    setConnectionType(type);
  }, []);

  return (
    <div ref={reactFlowWrapper} className="h-full w-full">
      {isEditing && (
        <Panel
          position="top-left"
          className="bg-cyberpunk-darker/90 p-2 rounded-md border border-purple-500/30 mb-4"
        >
          <div className="flex flex-col gap-2">
            <div className="flex gap-2 mb-2 items-center">
              <Button
                size="sm"
                className={`${
                  editMode === "select"
                    ? "bg-purple-600 hover:bg-purple-700 text-white"
                    : "bg-cyberpunk-darker border border-purple-500/30 text-purple-300 hover:bg-purple-900/20"
                }`}
                onClick={() => setEditMode("select")}
              >
                Select
              </Button>
              <Button
                size="sm"
                className={`${
                  editMode === "connect"
                    ? "bg-blue-600 hover:bg-blue-700 text-white"
                    : "bg-cyberpunk-darker border border-purple-500/30 text-blue-300 hover:bg-blue-900/20"
                }`}
                onClick={() => {
                  setEditMode("connect");
                  toast({
                    title: "Connect mode",
                    description:
                      "Click on a source node, then click on a target node to create a connection",
                  });
                }}
              >
                <LinkIcon className="h-4 w-4 mr-1" /> Connect
              </Button>
              <Button
                size="sm"
                className={`${
                  editMode === "delete"
                    ? "bg-red-600 hover:bg-red-700 text-white"
                    : "bg-cyberpunk-darker border border-red-500/30 text-red-300 hover:bg-red-900/20"
                }`}
                onClick={() => {
                  setEditMode("delete");
                  toast({
                    title: "Delete mode",
                    description: "Click on a node or connection to delete it",
                  });
                }}
              >
                <Trash className="h-4 w-4 mr-1" /> Delete
              </Button>
              <Button
                size="sm"
                className="bg-green-600 hover:bg-green-700 text-white"
                onClick={() => setIsAddCourseOpen(true)}
              >
                <Plus className="h-4 w-4 mr-1" /> Add Node
              </Button>

              <Button
                size="sm"
                className={`${
                  showEditorPanel
                    ? "bg-amber-600 hover:bg-amber-700 text-white"
                    : "bg-cyberpunk-darker border border-amber-500/30 text-amber-300 hover:bg-amber-900/20"
                }`}
                onClick={() => setShowEditorPanel(!showEditorPanel)}
              >
                <Palette className="h-4 w-4 mr-1" /> Style
              </Button>
            </div>
            <div className="flex gap-2 justify-between">
              <div className="flex gap-2">
                <Button
                  size="sm"
                  className="bg-cyberpunk-darker border border-purple-500/30 text-purple-300 hover:bg-purple-900/20"
                  onClick={handleUndo}
                  disabled={undoStack.length === 0}
                >
                  Undo
                </Button>
                <Button
                  size="sm"
                  className="bg-cyberpunk-darker border border-purple-500/30 text-purple-300 hover:bg-purple-900/20"
                  onClick={handleRedo}
                  disabled={redoStack.length === 0}
                >
                  Redo
                </Button>
              </div>

              <Button
                className="bg-purple-600 hover:bg-purple-700 text-white"
                size="sm"
                onClick={handleSaveRoadmap}
              >
                <Save className="h-4 w-4 mr-1" /> Save
              </Button>
            </div>
          </div>
        </Panel>
      )}

      {isEditing && showEditorPanel && (
        <Panel position="top-right" className="w-64">
          <RoadmapEditorPanel
            onStyleChange={handleStyleChange}
            onConnectionTypeChange={handleConnectionTypeChange}
            activeEdgeStyle={connectionType}
          />
        </Panel>
      )}

      <div className="h-full">
        <ReactFlow
          nodes={filteredNodes()}
          edges={edges}
          onNodesChange={isEditing ? onNodesChange : undefined}
          onEdgesChange={isEditing ? onEdgesChange : undefined}
          onConnect={isEditing ? onConnect : undefined}
          onNodeClick={onNodeClick}
          onEdgeClick={onEdgeClick}
          onPaneClick={onPaneClick}
          nodeTypes={nodeTypes}
          onNodeDragStop={onNodeDragStop}
          nodesDraggable={isEditing}
          nodesConnectable={isEditing && editMode === "connect"}
          elementsSelectable={!readOnly}
          defaultEdgeOptions={{
            type: "smoothstep",
            style: { stroke: "#6d28d9" },
            animated: true,
          }}
          fitView
        >
          <Controls />
          <MiniMap />
          <Background variant="dots" gap={12} size={1} color="#6d28d9" />
        </ReactFlow>
      </div>

      {selectedNode && (
        <CourseInfoPanel
          node={selectedNode}
          onClose={() => setSelectedNode(null)}
          onCompleteToggle={handleCompleteToggle}
          onDelete={isEditing ? handleDeleteNode : undefined}
          onUpdateStyle={handleUpdateNodeStyle}
          isAdmin={isEditing}
          readOnly={readOnly}
        />
      )}

      {isAddCourseOpen && (
        <AddCourseDialog
          open={isAddCourseOpen}
          onClose={() => setIsAddCourseOpen(false)}
          onAdd={handleAddCourse}
        />
      )}
    </div>
  );
}

export default function RoadmapView(props) {
  return (
    <ReactFlowProvider>
      <RoadmapViewInner {...props} />
    </ReactFlowProvider>
  );
}
