"use client";

import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { Button } from "../components/ui/button";
import { Input } from "../components/ui/input";
import { Label } from "../components/ui/label";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "../components/ui/card";
import { useToast } from "../components/ui/use-toast";
import { typeText } from "../lib/animations";
import anime from "animejs";

export default function RegisterPage() {
  const navigate = useNavigate();
  const { register } = useAuth();
  const { toast } = useToast();
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    // Apply typing effect to title
    typeText(".register-title", null, 800);

    // Subtle card animation
    const registerCard = document.querySelector(".register-card");
    if (registerCard) {
      anime({
        targets: registerCard,
        boxShadow: [
          "0 0 5px rgba(124, 58, 237, 0.3), 0 0 15px rgba(124, 58, 237, 0.2)",
          "0 0 2px rgba(124, 58, 237, 0.3), 0 0 10px rgba(124, 58, 237, 0.2)",
          "0 0 5px rgba(124, 58, 237, 0.3), 0 0 15px rgba(124, 58, 237, 0.2)",
        ],
        duration: 2000,
        direction: "alternate",
        loop: true,
        easing: "easeInOutSine",
      });
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      toast({
        title: "Password mismatch",
        description: "Passwords do not match",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      await register({ username, email, password });
      toast({
        title: "Registration successful",
        description: "Your account has been created successfully",
      });
      navigate("/");
    } catch (error) {
      toast({
        title: "Registration failed",
        description: error.message || "An error occurred during registration",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container flex items-center justify-center min-h-[80vh] py-8">
      <div className="absolute inset-0 bg-cyber-grid bg-[length:50px_50px] opacity-10 pointer-events-none"></div>
      <Card className="w-full max-w-md register-card border-violet-500/30 shadow-lg bg-gradient-to-br from-cyberpunk-darker to-cyberpunk-dark">
        <CardHeader className="space-y-2 pb-4 border-b border-violet-500/20">
          <CardTitle className="text-2xl font-bold font-cyber text-violet-300 register-title">
            Create Account
          </CardTitle>
          <CardDescription className="font-mono-cyber text-gray-400">
            Register to track your learning progress
          </CardDescription>
        </CardHeader>
        <form onSubmit={handleSubmit}>
          <CardContent className="space-y-6 pt-6">
            <div className="space-y-3">
              <Label htmlFor="username" className="font-cyber text-gray-300">
                Username
              </Label>
              <Input
                id="username"
                placeholder="johndoe"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
                className="bg-cyberpunk-darker/50 border-violet-500/30 focus:border-violet-500/60 text-white"
              />
            </div>
            <div className="space-y-3">
              <Label htmlFor="email" className="font-cyber text-gray-300">
                Email
              </Label>
              <Input
                id="email"
                type="email"
                placeholder="your.email@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                className="bg-cyberpunk-darker/50 border-violet-500/30 focus:border-violet-500/60 text-white"
              />
            </div>
            <div className="space-y-3">
              <Label htmlFor="password" className="font-cyber text-gray-300">
                Password
              </Label>
              <Input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className="bg-cyberpunk-darker/50 border-violet-500/30 focus:border-violet-500/60 text-white"
              />
            </div>
            <div className="space-y-3">
              <Label
                htmlFor="confirmPassword"
                className="font-cyber text-gray-300"
              >
                Confirm Password
              </Label>
              <Input
                id="confirmPassword"
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
                className="bg-cyberpunk-darker/50 border-violet-500/30 focus:border-violet-500/60 text-white"
              />
            </div>
          </CardContent>
          <CardFooter className="flex flex-col pt-2">
            <Button
              type="submit"
              className="w-full bg-violet-600 hover:bg-violet-700 text-white font-cyber"
              disabled={isLoading}
            >
              {isLoading ? "Creating account..." : "Sign Up"}
            </Button>
            <p className="mt-4 text-center text-sm text-gray-400 font-mono-cyber">
              Already have an account?{" "}
              <Link
                to="/login"
                className="text-violet-400 hover:text-violet-300"
              >
                Sign in
              </Link>
            </p>
          </CardFooter>
        </form>
      </Card>
    </div>
  );
}
