import * as anime from "animejs";

/**
 * Adds a glitch effect to elements
 * @param {string} selector - CSS selector for target elements
 */
export const glitchAnimation = selector => {
  const elements = document.querySelectorAll(selector);
  elements.forEach(el => {
    // Set data-text attribute for pseudo-elements
    if (!el.hasAttribute("data-text")) {
      el.setAttribute("data-text", el.textContent);
    }

    // Add glitch-text class if not present
    if (!el.classList.contains("glitch-text")) {
      el.classList.add("glitch-text");
    }
  });
};

/**
 * Creates a neon pulse effect for buttons or borders
 * @param {string} selector - CSS selector for target elements
 * @param {string} color - Hexadecimal color code for the neon effect
 */
export const neonPulse = (selector, color = "#00f6ff") => {
  anime.animate({
    targets: selector,
    boxShadow: [`0 0 5px ${color}, 0 0 10px ${color}`, `0 0 2px ${color}, 0 0 5px ${color}`, `0 0 5px ${color}, 0 0 10px ${color}`],
    duration: 1500,
    easing: "easeInOutSine",
    direction: "alternate",
    loop: true
  });
};

/**
 * Animate text typing effect
 * @param {string} selector - CSS selector for target element
 * @param {string} text - Text to type (defaults to element's content)
 * @param {number} duration - Animation duration in ms
 */
export const typeText = (selector, text = null, duration = 1000) => {
  const el = document.querySelector(selector);
  if (!el) return;
  const content = text || el.textContent;
  el.textContent = "";
  el.style.opacity = 1;
  let i = 0;
  const interval = duration / content.length;
  const timer = setInterval(() => {
    if (i < content.length) {
      el.textContent += content.charAt(i);
      i++;
    } else {
      clearInterval(timer);
    }
  }, interval);
};

/**
 * Creates a scan line effect that moves across the element
 * @param {string} selector - CSS selector for target elements
 */
export const scanLineEffect = selector => {
  const elements = document.querySelectorAll(selector);
  elements.forEach(el => {
    // Create scan line element
    const scanLine = document.createElement("div");
    scanLine.classList.add("cyber-scan-line");
    scanLine.style.position = "absolute";
    scanLine.style.top = "0";
    scanLine.style.left = "0";
    scanLine.style.width = "100%";
    scanLine.style.height = "4px";
    scanLine.style.background = "rgba(0, 246, 255, 0.3)";
    scanLine.style.zIndex = "10";

    // Ensure parent has position relative
    if (getComputedStyle(el).position === "static") {
      el.style.position = "relative";
    }
    el.style.overflow = "hidden";
    el.appendChild(scanLine);

    // Animate scan line
    anime.animate({
      targets: scanLine,
      translateY: ["0%", "100%"],
      duration: 2000,
      easing: "linear",
      loop: true
    });
  });
};

/**
 * Creates a mecha-inspired panel transform effect
