    anime({
      targets: selector,
      boxShadow: [`0 0 5px ${color}, 0 0 10px ${color}`, `0 0 2px ${color}, 0 0 5px ${color}`, `0 0 5px ${color}, 0 0 10px ${color}`],
      duration: 1500,
      easing: "easeInOutSine",
      direction: "alternate",
      loop: true
    });
  } catch (error) {
    console.error("Error in neonPulse:", error);
  }
};

/**
 * Animate text typing effect
 * @param {string} selector - CSS selector for target element
 * @param {string} text - Text to type (defaults to element's content)
 * @param {number} duration - Animation duration in ms
 */
export const typeText = (selector, text = null, duration = 1000) => {
  try {
    const el = document.querySelector(selector);
    if (!el) return;
    const content = text || el.textContent;
    el.textContent = "";
    el.style.opacity = 1;
    let i = 0;
    const interval = duration / content.length;
    const timer = setInterval(() => {
      if (i < content.length) {
