import api from "./api";

// Get course with full roadmap data (nodes and edges)
export const getCourseRoadmap = async (courseId) => {
  try {
    console.log(`Fetching roadmap data for course ${courseId}`);

    // Get the course data with nodes and edges
    const courseResponse = await api.get(`/courses/${courseId}`);
    const courseData = courseResponse.data;

    // Get nodes separately for full data
    const nodesResponse = await api.get(`/courses/${courseId}/nodes`);
    const nodesData = nodesResponse.data;

    // Get edges separately for full data
    const edgesResponse = await api.get(`/courses/${courseId}/edges`);
    const edgesData = edgesResponse.data;

    // Combine all data
    const roadmapData = {
      ...courseData,
      nodes: transformNodesToClientFormat(nodesData || []),
      edges: transformEdgesToClientFormat(edgesData || []),
    };

    console.log(`Got roadmap data for course ${courseId}:`, roadmapData);
    return roadmapData;
  } catch (error) {
    console.error(`Error fetching roadmap for course ${courseId}:`, error);
    throw error;
  }
};

// Save course roadmap data (nodes and edges)
export const saveCourseRoadmap = async (courseId, roadmapData) => {
  try {
    console.log(`Saving roadmap data for course ${courseId}`);

    if (!courseId || isNaN(Number(courseId))) {
      throw new Error(`Invalid course ID: ${courseId}`);
    }

    const numericCourseId = Number(courseId);
    const { nodes, edges } = roadmapData;

    console.log("Received nodes:", nodes);
    console.log("Received edges:", edges);

    // Validate nodes and edges
    if (!Array.isArray(nodes)) {
      throw new Error("Nodes must be an array");
    }

    if (!Array.isArray(edges)) {
      throw new Error("Edges must be an array");
    }

    // Since this is a course roadmap, we need to associate nodes and edges with a roadmap
    // First, check if the course already has a roadmap
    const response = await api.get(`/courses/${numericCourseId}`);
    const courseData = response.data;

    // Let's create a roadmap for this course if it doesn't exist or use the existing one
    let roadmapId;

    // Use the first roadmap we find or create a new one
    if (courseData.roadmapId) {
      roadmapId = courseData.roadmapId;
      console.log(`Using existing roadmap ID: ${roadmapId}`);
    } else {
      // Create a new roadmap for this course
      const roadmapResponse = await api.post("/roadmaps", {
        title: `Roadmap for ${courseData.title}`,
        description: `Learning roadmap for course: ${courseData.title}`,
        categoryId: courseData.categoryId,
        skillId: courseData.skillId,
      });

      roadmapId = roadmapResponse.data.id;

      // Associate the roadmap with the course
      await api.put(`/courses/${numericCourseId}`, {
        roadmapId: roadmapId,
      });

      console.log(`Created new roadmap with ID: ${roadmapId}`);
    }

    // Now properly transform the nodes and edges to include the roadmapId
    const transformedNodes = transformNodesToServerFormat(nodes).map(
      (node) => ({
        ...node,
        roadmapId: roadmapId,
      })
    );

    const transformedEdges = transformEdgesToServerFormat(edges).map(
      (edge) => ({
        ...edge,
        roadmapId: roadmapId,
      })
    );

    console.log("Transformed nodes with roadmapId:", transformedNodes);
    console.log("Transformed edges with roadmapId:", transformedEdges);

    // Save to the roadmap endpoints instead of course endpoints
    if (transformedNodes.length > 0) {
      console.log(
        `Saving ${transformedNodes.length} nodes to roadmap ${roadmapId}`
      );
      await api.post(`/roadmaps/${roadmapId}/nodes`, {
        nodes: transformedNodes,
      });
    } else {
      console.log("No nodes to save");
    }

    if (transformedEdges.length > 0) {
      console.log(
        `Saving ${transformedEdges.length} edges to roadmap ${roadmapId}`
      );
      await api.post(`/roadmaps/${roadmapId}/edges`, {
        edges: transformedEdges,
      });
    } else {
      console.log("No edges to save");
    }

    console.log("Successfully saved nodes and edges to roadmap");

    // Get updated data
    return await getCourseRoadmap(numericCourseId);
  } catch (error) {
    console.error(`Error saving roadmap for course ${courseId}:`, error);
    throw error;
  }
};

// Create a new course with roadmap data
export const createCourseWithRoadmap = async (courseData) => {
  try {
    console.log("Creating new course with roadmap data");
    const { nodes, edges, ...courseDetails } = courseData;

    // Create the course first
    const courseResponse = await api.post("/courses", courseDetails);
    const newCourse = courseResponse.data;
    const courseId = newCourse.id;

    // Then save nodes and edges if provided
    if ((nodes && nodes.length > 0) || (edges && edges.length > 0)) {
      await saveCourseRoadmap(courseId, { nodes, edges });
    }

    // Return the full course with roadmap data
    return await getCourseRoadmap(courseId);
  } catch (error) {
    console.error("Error creating course with roadmap:", error);
    throw error;
  }
};

// Transform nodes from server format to client format
export const transformNodesToClientFormat = (nodes) => {
  return nodes
    .map((node) => {
      try {
        // If node already has client format, return as is
        if (node.type && node.position && node.data) {
          return node;
        }

        // Parse the data if it's a string
        let nodeData = node.data;
        if (typeof node.data === "string") {
          try {
            nodeData = JSON.parse(node.data);
          } catch (e) {
            nodeData = { label: node.data };
          }
        }

        return {
          id: node.nodeIdentifier,
          type: "courseNode", // Default node type
          position: {
            x: node.positionX || 0,
            y: node.positionY || 0,
          },
          data: {
            ...nodeData,
            id: node.nodeIdentifier,
          },
        };
      } catch (error) {
        console.error("Error transforming node to client format:", error, node);
        return null;
      }
    })
    .filter(Boolean);
};

// Transform nodes from client format to server format
export const transformNodesToServerFormat = (nodes) => {
  if (!Array.isArray(nodes)) {
    console.error("Nodes is not an array:", nodes);
    return [];
  }

  return nodes
    .map((node) => {
      try {
        if (!node || !node.id) {
          console.warn("Invalid node object:", node);
          return null;
        }

        // Make sure position exists and has valid x, y values
        const posX = node.position?.x || 0;
        const posY = node.position?.y || 0;

        // Ensure data is properly converted to string if it's an object
        let nodeData;
        if (typeof node.data === "string") {
          nodeData = node.data;
        } else if (typeof node.data === "object") {
          try {
            nodeData = JSON.stringify(node.data || {});
          } catch (e) {
            console.error("Error stringifying node data:", e);
            nodeData = JSON.stringify({ label: "Error parsing data" });
          }
        } else {
          nodeData = JSON.stringify({ label: "Untitled" });
        }

        return {
          nodeIdentifier: node.id,
          positionX: posX,
          positionY: posY,
          data: nodeData,
        };
      } catch (error) {
        console.error("Error transforming node to server format:", error, node);
        return null;
      }
    })
    .filter(Boolean);
};

// Transform edges from server format to client format
export const transformEdgesToClientFormat = (edges) => {
  return edges
    .map((edge) => {
      try {
        // If edge already has client format, return as is
        if (edge.source && edge.target && edge.id) {
          return edge;
        }

        // Parse the style if it's a string
        let style = {};
        if (edge.style) {
          try {
            if (typeof edge.style === "string") {
              style = JSON.parse(edge.style);
            } else if (typeof edge.style === "object") {
              style = edge.style;
            }
          } catch (e) {
            style = {};
          }
        }

        return {
          id: edge.edgeIdentifier,
          source: edge.source,
          target: edge.target,
          type: edge.type || "smoothstep",
          animated: edge.animated || false,
          style: {
            stroke: style.stroke || "#6d28d9",
            strokeWidth: style.strokeWidth || 1,
            ...style,
          },
        };
      } catch (error) {
        console.error("Error transforming edge to client format:", error, edge);
        return null;
      }
    })
    .filter(Boolean);
};

// Transform edges from client format to server format
export const transformEdgesToServerFormat = (edges) => {
  if (!Array.isArray(edges)) {
    console.error("Edges is not an array:", edges);
    return [];
  }

  return edges
    .map((edge) => {
      try {
        if (!edge || !edge.id || !edge.source || !edge.target) {
          console.warn("Invalid edge object:", edge);
          return null;
        }

        // Ensure style is properly converted to string if it's an object
        let edgeStyle;
        if (typeof edge.style === "string") {
          edgeStyle = edge.style;
        } else if (typeof edge.style === "object") {
          try {
            edgeStyle = JSON.stringify(edge.style || {});
          } catch (e) {
            console.error("Error stringifying edge style:", e);
            edgeStyle = JSON.stringify({});
          }
        } else {
          edgeStyle = JSON.stringify({});
        }

        return {
          edgeIdentifier: edge.id,
          source: edge.source,
          target: edge.target,
          type: edge.type || "smoothstep",
          animated: Boolean(edge.animated),
          style: edgeStyle,
        };
      } catch (error) {
        console.error("Error transforming edge to server format:", error, edge);
        return null;
      }
    })
    .filter(Boolean);
};

export default {
  getCourseRoadmap,
  saveCourseRoadmap,
  createCourseWithRoadmap,
  transformNodesToClientFormat,
  transformNodesToServerFormat,
  transformEdgesToClientFormat,
  transformEdgesToServerFormat,
};
