import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "./components/ui/toaster";
import { AuthProvider } from "./context/AuthContext";
import { useEffect, useState } from "react";
import { initCyberpunkAnimations } from "./lib/animations";

// Pages
import Home from "./pages/Home";
import RoadmapsPage from "./pages/RoadmapsPage";
import RoadmapDetailPage from "./pages/RoadmapDetailPage";
import RoadmapEditPage from "./pages/RoadmapEditPage";
import CreateRoadmapPage from "./pages/CreateRoadmapPage";
import LoginPage from "./pages/LoginPage";
import RegisterPage from "./pages/RegisterPage";
import ProfilePage from "./pages/ProfilePage";
import AdminSkillsPage from "./pages/AdminSkillsPage";
import AdminCategoriesPage from "./pages/AdminCategoriesPage";
import NotFoundPage from "./pages/NotFoundPage";

// Components
import MainNav from "./components/MainNav";
import ProtectedRoute from "./components/ProtectedRoute";
import AdminRoute from "./components/AdminRoute";

// Create a client
const queryClient = new QueryClient();

// Error boundary component
const ErrorBoundary = ({ children }) => {
  const [hasError, setHasError] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const errorHandler = (event) => {
      console.error("Global error caught:", event.error || event.message);
      setError(
        (event.error && event.error.message) ||
          event.message ||
          "An unexpected error occurred"
      );
      setHasError(true);
      event.preventDefault(); // Prevent default error handling
    };

    window.addEventListener("error", errorHandler);
    window.addEventListener("unhandledrejection", errorHandler);

    return () => {
      window.removeEventListener("error", errorHandler);
      window.removeEventListener("unhandledrejection", errorHandler);
    };
  }, []);

  if (hasError) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl text-destructive mb-4">Something went wrong</h2>
        <p className="mb-4">{error}</p>
        <button
          className="btn-cyber px-4 py-2"
          onClick={() => window.location.reload()}
        >
          Reload Application
        </button>
      </div>
    );
  }

  return children;
};

// Apply fallback styles for when animations fail
const applyFallbackStyles = () => {
  // Create a style element
  const style = document.createElement("style");

  // Add CSS for fallback animations
  style.textContent = `
    .btn-cyber {
      box-shadow: 0 0 5px #00f6ff, 0 0 10px #00f6ff;
      animation: fallbackPulse 1.5s infinite alternate;
    }
    .btn-cyber-pink {
      box-shadow: 0 0 5px #f700ff, 0 0 10px #f700ff;
      animation: fallbackPulse 1.5s infinite alternate;
    }
    .btn-cyber-yellow {
      box-shadow: 0 0 5px #ffde00, 0 0 10px #ffde00;
      animation: fallbackPulse 1.5s infinite alternate;
    }
    @keyframes fallbackPulse {
      0% { opacity: 1; }
      50% { opacity: 0.7; }
      100% { opacity: 1; }
    }
  `;

  // Append to document head
  document.head.appendChild(style);
};

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [animationFailed, setAnimationFailed] = useState(false);

  useEffect(() => {
    // Initialize cyberpunk animations after the DOM is fully loaded
    try {
      const timer = setTimeout(() => {
        try {
          console.log("Initializing animations...");
          initCyberpunkAnimations();
          setIsLoading(false);
        } catch (error) {
          console.error("Animation initialization failed:", error);
          setAnimationFailed(true);
          applyFallbackStyles();
          setIsLoading(false);
        }
      }, 1200); // Increased delay to ensure DOM is fully ready

      return () => clearTimeout(timer);
    } catch (error) {
      console.error("Failed to setup animation timer:", error);
      setAnimationFailed(true);
      applyFallbackStyles();
      setIsLoading(false);
    }
  }, []);

  return (
    <QueryClientProvider client={queryClient}>
      <AuthProvider>
        <ErrorBoundary>
          <Router>
            <div className="min-h-screen bg-background font-sans antialiased">
              <div className="relative flex min-h-screen flex-col">
                <MainNav />
                <div className="flex-1">
                  {isLoading ? (
                    <div className="flex items-center justify-center h-[80vh]">
                      <div className="text-center">
                        <h2 className="neon-text text-xl mb-4">
                          INITIALIZING SYSTEM
                        </h2>
                        <div className="w-64 h-2 bg-muted relative overflow-hidden rounded">
                          <div
                            className="absolute top-0 left-0 h-full bg-secondary animate-pulse"
                            style={{
                              width: "100%",
                              animation: "pulse 1.5s infinite",
                            }}
                          ></div>
                        </div>
                      </div>
                    </div>
                  ) : (
                    <Routes>
                      <Route path="/" element={<Home />} />
                      <Route path="/login" element={<LoginPage />} />
                      <Route path="/register" element={<RegisterPage />} />
                      <Route path="/roadmaps" element={<RoadmapsPage />} />
                      <Route
                        path="/roadmaps/:id"
                        element={<RoadmapDetailPage />}
                      />

                      {/* Protected routes */}
                      <Route
                        path="/roadmaps/create"
                        element={
                          <ProtectedRoute>
                            <CreateRoadmapPage />
                          </ProtectedRoute>
                        }
                      />
                      <Route
                        path="/roadmaps/:id/edit"
                        element={
                          <ProtectedRoute>
                            <RoadmapEditPage />
                          </ProtectedRoute>
                        }
                      />
                      <Route
                        path="/profile"
                        element={
                          <ProtectedRoute>
                            <ProfilePage />
                          </ProtectedRoute>
                        }
                      />

                      {/* Admin routes */}
                      <Route
                        path="/admin/skills"
                        element={
                          <AdminRoute>
                            <AdminSkillsPage />
                          </AdminRoute>
                        }
                      />
                      <Route
                        path="/admin/categories"
                        element={
                          <AdminRoute>
                            <AdminCategoriesPage />
                          </AdminRoute>
                        }
                      />

                      {/* 404 page */}
                      <Route path="*" element={<NotFoundPage />} />
                    </Routes>
                  )}
                </div>
              </div>
              <Toaster />
            </div>
          </Router>
        </ErrorBoundary>
      </AuthProvider>
    </QueryClientProvider>
  );
}

export default App;
