import { Link } from "react-router-dom";
import { Button } from "../components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
  CardFooter,
} from "../components/ui/card";
// Tabs components removed - not currently used
// import {
//   Tabs,
//   TabsContent,
//   TabsList,
//   TabsTrigger,
// } from "../components/ui/tabs";
import { useQuery } from "@tanstack/react-query";
import { getRoadmaps } from "../services/roadmapService";
import { useEffect } from "react";
import { typeText } from "../lib/animations";
import {
  TrendingUp,
  Clock,
  CheckCircle2,
  BookOpen,
  CirclePlus,
} from "lucide-react"; // Added icons

export default function Home() {
  // Uncomment to use actual roadmaps data
  const {
    data: roadmaps,
    isLoading,
    error,
  } = useQuery({
    queryKey: ["roadmaps"],
    queryFn: getRoadmaps,
  });

  useEffect(() => {
    // Apply typing effect to hero title after component mounts
    const timer = setTimeout(() => {
      typeText(".hero-title", null, 1200);
    }, 800);

    return () => clearTimeout(timer);
  }, []);

  return (
    <main>
      {/* 顶部导航标签 */}
      <div className="bg-muted/50">
        <div className="container mx-auto">
          <div className="flex overflow-x-auto py-3 gap-2">
            <Button variant="ghost" className="text-foreground font-semibold">
              All Roadmaps
            </Button>
            <Button variant="ghost" className="text-muted-foreground">
              Nền tảng
            </Button>
            <Button variant="ghost" className="text-muted-foreground">
              Frontend
            </Button>
            <Button variant="ghost" className="text-muted-foreground">
              Backend
            </Button>
            <Button variant="ghost" className="text-muted-foreground">
              Database
            </Button>
            <Button variant="ghost" className="text-muted-foreground">
              DevOps
            </Button>
            <Button variant="ghost" className="text-muted-foreground">
              Mobile
            </Button>
            <Button variant="ghost" className="text-muted-foreground">
              AI/ML
            </Button>
            <Button variant="ghost" className="text-muted-foreground">
              Security
            </Button>
          </div>
        </div>
      </div>

      <div className="container mx-auto py-8 px-4">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-2xl font-bold text-foreground">
            <span className="text-primary">Role</span> Based Roadmaps
          </h1>
          <Button variant="default" className="rounded-full">
            <CirclePlus className="mr-2 h-5 w-5" />
            Create
          </Button>
        </div>

        {/* 三列角色卡片布局 */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {/* Web Development */}
          <CategoryCard
            title="Web Development"
            description="Full-stack web development learning paths"
            links={[
              { name: "Web Development", tag: "Frontend" },
              { name: "JavaScript", tag: "Frontend" },
              { name: "Python", tag: "Backend" },
            ]}
          />

          {/* Data Science */}
          <CategoryCard
            title="Data Science"
            description="Data analysis, machine learning, and AI"
            links={[{ name: "Data Science", tag: "" }]}
          />

          {/* Cybersecurity */}
          <CategoryCard
            title="Cybersecurity"
            description="Network security, ethical hacking, and defense"
            links={[{ name: "Cybersecurity", tag: "" }]}
          />

          {/* DevOps */}
          <CategoryCard
            title="DevOps"
            description="CI/CD, infrastructure as code, and cloud services"
            links={[{ name: "DevOps Engineering", tag: "DevOps" }]}
          />

          {/* Mobile Development */}
          <CategoryCard
            title="Mobile Development"
            description="iOS, Android, and cross-platform app development"
            links={[{ name: "Mobile Development", tag: "Mobile" }]}
          />
        </div>
      </div>

      {/* Your Progress Section */}
      <section className="mb-16">
        <div className="flex flex-col md:flex-row justify-between items-center mb-8">
          <h2 className="text-2xl font-bold tracking-tight text-primary">
            Your Progress
          </h2>
          <p className="text-muted-foreground">Track your learning journey</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Day Streak Card */}
          <Card>
            <CardContent className="flex flex-col items-center justify-center p-6">
              <TrendingUp className="h-8 w-8 text-accent mb-3" />
              <div className="text-4xl font-bold text-foreground mb-1">
                1
              </div>{" "}
              {/* text-accent to text-foreground */}
              <div className="text-sm text-muted-foreground">Day Streak</div>
            </CardContent>
          </Card>

          {/* Today Card */}
          <Card>
            <CardContent className="flex flex-col items-center justify-center p-6">
              <Clock className="h-8 w-8 text-accent mb-3" />
              <div className="text-4xl font-bold text-foreground mb-1">
                0
              </div>{" "}
              {/* text-accent to text-foreground */}
              <div className="text-sm text-muted-foreground">Today</div>
            </CardContent>
          </Card>

          {/* Completed Card */}
          <Card>
            <CardContent className="flex flex-col items-center justify-center p-6">
              <CheckCircle2 className="h-8 w-8 text-accent mb-3" />
              <div className="text-4xl font-bold text-foreground mb-1">
                5
              </div>{" "}
              {/* text-accent to text-foreground */}
              <div className="text-sm text-muted-foreground">Completed</div>
            </CardContent>
          </Card>
        </div>
      </section>

      {/* Roadmaps Section */}
      <div className="mb-12">
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-8 gap-4">
          <h2 className="text-2xl font-bold tracking-tight text-primary">
            Role Based Roadmaps
          </h2>
          <Link to="/roadmaps/create">
            <Button variant="outline">Create Roadmap</Button>
          </Link>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {isLoading ? (
            // Hiển thị trạng thái loading
            <div className="col-span-4 text-center py-12">
              <div className="text-muted-foreground">Loading roadmaps...</div>
            </div>
          ) : error ? (
            // Hiển thị lỗi nếu có
            <div className="col-span-4 text-center py-12">
              <div className="text-destructive">
                Error loading roadmaps: {error.message}
              </div>
            </div>
          ) : roadmaps && roadmaps.length > 0 ? (
            // Hiển thị roadmaps từ API
            roadmaps
              .slice(0, 4)
              .map((roadmap) => (
                <RoadmapCard
                  key={roadmap.id}
                  title={roadmap.title}
                  description={roadmap.description}
                  courseCount={roadmap.courseCount || 0}
                  id={roadmap.id}
                />
              ))
          ) : (
            // Hiển thị khi không có roadmap nào
            <div className="col-span-4 text-center py-12">
              <div className="text-muted-foreground">
                No roadmaps found. Create your first roadmap!
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Skill Based Section */}
      <div className="mb-12">
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-8 gap-4">
          <h2 className="text-2xl font-bold tracking-tight text-primary">
            Skill Based Roadmaps
          </h2>
          <p className="text-muted-foreground">
            Focused learning paths for specific technologies
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          <SkillCard
            title="IT Fundamentals"
            description="Core IT concepts and foundational knowledge"
            count={15}
          />
          <SkillCard
            title="Cybersecurity"
            description="Network security, ethical hacking, and defense"
            count={14}
          />
          <SkillCard
            title="Mobile Development"
            description="iOS, Android, and cross-platform app development"
            count={9}
          />
        </div>
      </div>

      <footer className="mt-16 pt-8 text-center text-sm text-muted-foreground border-t border-[hsl(var(--border))]">
        {" "}
        {/* Updated classes */}
        <p>© 2023 CyberPath. All rights reserved.</p>{" "}
        {/* font-mono-cyber removed by ensuring parent doesn't set it, inherits Open Sans */}
      </footer>
    </main>
  );
}

function RoadmapCard({ title, description, courseCount, id }) {
  return (
    <Card>
      <CardHeader className="pb-2">
        <CardTitle className="text-lg font-heading text-primary">
          {" "}
          {/* Added font-heading */}
          {title}
        </CardTitle>
        <CardDescription className="line-clamp-2 text-muted-foreground">
          {description}
        </CardDescription>
      </CardHeader>
      <CardContent className="p-4">
        <div className="flex justify-between items-center">
          <div className="text-xs text-muted-foreground">
            {courseCount} courses
          </div>
          <Button asChild variant="outline" size="sm">
            <Link to={`/roadmaps/${id}`}>View</Link>
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

function SkillCard({ title, description, count }) {
  return (
    <Card>
      <CardHeader className="pb-2">
        <CardTitle className="font-heading text-primary">{title}</CardTitle>{" "}
        {/* Added font-heading */}
        <CardDescription className="text-muted-foreground">
          {description}
        </CardDescription>
      </CardHeader>
      <CardContent className="p-4">
        <div className="flex justify-between items-center">
          <div className="text-xs text-muted-foreground">{count} roadmaps</div>
          <Button asChild variant="outline" size="sm">
            <Link to="/roadmaps">View</Link>
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

function CategoryCard({ title, description, links }) {
  return (
    <Card className="roadmap-card border-none shadow-sm hover:shadow-md transition-all">
      <CardHeader>
        <CardTitle className="text-xl font-semibold text-foreground">
          {title}
        </CardTitle>
        <CardDescription className="text-muted-foreground">
          {description}
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-3 pt-0">
        {links.map((link, index) => (
          <div key={index} className="flex items-center">
            <BookOpen className="h-4 w-4 mr-2 text-muted-foreground" />
            <span className="text-foreground">{link.name}</span>
            {link.tag && (
              <span className={`ml-auto tag tag-${link.tag.toLowerCase()}`}>
                {link.tag}
              </span>
            )}
          </div>
        ))}
      </CardContent>
    </Card>
  );
}
